get_context().precision = 8192


x = mpz(
    0x152A1447B61D023BEBAB7B1F8BC9D934C2D4B0C8EF7E211DBBCF841136D030E3C829F222CEC318F6F624EB529B54BCDA848F65574896D70CD6C3460D0C9064CD66E826578C2035AB63DA67D069FA302227A9012422D2402F8F0D4495EF66104EBD774F341AA62F493184301DEBF910AB3D1E72E357A99C460370254F3DFCCD9AE
)
n = mpz(
    
)
c = mpz(
    
)

# p + q = x, pq = n
# p^2 - xp + n = 0 (p can be q as well)
# p = x/2 + isqrt(x^2 - 4*n)/2
p = mpz(x / 2 + isqrt(x**2 - 4 * n) / 2)
assert n % p == 0

q = x - p
e = 65537

phi = (p - 1) * (q - 1)
d = pow(e, -1, phi)

m = int(pow(c, d, n))
m_bytes = (m.bit_length() + 7) // 8
print(m.to_bytes(m_bytes, "big"))
